import React from "react";

const OfferBalloon = ({ content }) => {
  const balloonStyles = {
    position: "relative",
    display: "inline-block",
  };

  const tooltipStyles = {
    visibility: "hidden",
    width: "120px",
    backgroundColor: "black",
    color: "#fff",
    textAlign: "center",
    borderRadius: "6px",
    padding: "5px",
    position: "absolute",
    zIndex: "1",
    bottom: "125%",
    left: "50%",
    marginLeft: "-60px", // Adjust this value to center the tooltip horizontally
  };

  const showTooltip = () => {
    tooltipStyles.visibility = "visible";
  };

  const hideTooltip = () => {
    tooltipStyles.visibility = "hidden";
  };

  return (
    <div className="offer-balloon" style={balloonStyles}>
      <span
        className="tooltip"
        style={tooltipStyles}
        onMouseEnter={showTooltip}
        onMouseLeave={hideTooltip}
      >
        {content}
      </span>
    </div>
  );
};

export default OfferBalloon;
