import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import CartProduct from "../component/cartProduct";
import emptyCartImage from "../assest/empty3.jpg";
import { toast } from "react-hot-toast";
import { deleteCartItem } from "../redux/productSlide";
import "./cart.css";
import logo from "../assest/logo.png";

const Cart = () => {
  const dispatch = useDispatch();
  const productCartItem = useSelector((state) => state.product.cartItem);
  const user = useSelector((state) => state.user);

  const isLoggedIn = !!user && !!user.email;

  const totalPrice = productCartItem.reduce((acc, curr) => acc + parseInt(curr.total), 0);
  const totalQty = productCartItem.reduce((acc, curr) => acc + curr.qty, 0);

 
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState({
    name: "",
    price: "",
    quantity: "",
    total: "",
    razorpay_payment_id: "",
    // Add other relevant cart item details here
  });

  const sendDataToBackend = async () => {
    try {
      setLoading(true);
      
      // Use 'data' state object in your fetch request or wherever it's needed
      const cartData = productCartItem.map((item) => ({
        name: item.name,
        price: item.price,
        quantity: item.qty,
        total: item.total,
        razorpay_payment_id: item.razorpay_payment_id,
        // Add other relevant cart item details here
      }));

      const response = await fetch('/store-cart-details', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ cartItems: cartData }),
      });

      if (response.ok) {
        const responseData = await response.json();
        console.log('Cart details sent successfully:', responseData);
        toast.success('Cart details sent successfully');
        // Reset cart after successful submission (you might want to dispatch an action to clear the cart)
      } else {
        throw new Error('Failed');
      }
    } catch (error) {
      console.error('Error sending cart details:', error);
      toast.error('Failed to send cart details');
    } finally {
      setLoading(false);
    }
  };
  

  const openRazorpay = () => {
    if (!isLoggedIn) {
      // If user is not logged in, redirect to login page
      // You can implement your own logic for redirecting to the login page
      // For example:
      // Replace '/login' with your actual login page route
      window.location.href = '/login';
      return; // Prevent further execution
    }

    sendDataToBackend();
    // const handleLogout = () => {
    //   // Clear cart items on logout
    //   dispatch(logoutRedux());
    //   productCartItem.forEach((item) => {
    //     dispatch(deleteCartItem(item.id)); // Assuming deleteCartItem action takes an 'id' parameter
    //   });
    // };
    const options = {
      key: "rzp_test_BE6renshHuqsYS", // Replace with your Razorpay key
      amount: totalPrice * 100, // Total amount in paisa
      currency: "INR",
      name: "AGRIDUCT",
      description: "Payment for items",
      image: logo, // Replace with your logo URL
      handler: function (response) {
        // Handle successful payment here
        toast.success("Payment successful! Order ID: " + response.razorpay_payment_id);
        // You can perform further actions here after successful payment
      },
      prefill: {
        name: user.name, // Pass user's name here
        email: user.email, // Pass user's email here
        // contact: user.contactNumber // Pass user's contact number here
      },
      theme: {
        color: "#3399cc"
      }
    };
    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="i">
      <div className="car1 p-5 md:p-4">
        <h2 className="text-lg md:text-6xl font-bold text-yellow-500">
          Your
          <span className="text-white"> Cart Items</span>
        </h2>

        {productCartItem[0] ?
        
          <div className="my-5 flex gap">
            {/* display cart items */}
            <div className="md:w-3/4  flex-col  p-4 justify-center ">
              {productCartItem.map((el) => {
                return (
                  <CartProduct
                    key={el._id}
                    id={el._id}
                    name={el.name}
                    image={el.image}
                    category={el.category}
                    farmername={el.far}
                    quantity={el.quantity}
                    qty={el.qty}
                    total={el.total}
                    price={el.price}
                  />
                );
              })}
            </div>

            {/* total cart item */}
            <div className="cart2 w-full max-w-md p-7 ml-auto mx-10 h-fullnpm border-8 border-yellow-300">
              <h2 className="bg-yellow-500 text-white p-2 text-lg">Summary</h2>
              <div className="flex w-full py-2 text-lg text-white font-bold border-b">
                <p>Total Qty :</p>
                <p className="ml-auto w-32 font-bold">
                  <span>{totalQty}</span>
                  <span className="font-bold text-yellow-500">Kg</span>
                </p>
              </div>
              <div className="flex w-full py-2 text-lg text-white font-bold border-b">
                <p>Total Price :</p>
                <p className="ml-auto w-32 font-bold">
                  <span className="text-yellow-500">₹</span> {totalPrice}
                </p>
              </div>
              <button className="bg-yellow-500 w-full text-lg font-bold py-2 text-white"onClick={openRazorpay}>
                {isLoggedIn ? 'Pay' : 'Login to Pay'}
              </button>
            </div>
          </div>
          :
          <div className="flex w-full justify-center items-center flex-col">
            <img src={emptyCartImage} className="w-full max-w-sm" alt="Empty Cart" />
            <p className="text-yellow-500 text-3xl font-bold">Empty Cart</p>
          </div>
        }
      </div>
    </div>
  );
};

export default Cart;
