import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import CardFeature from "../component/CardFeature";
import FilterProduct from "../component/FilterProduct";

const AllProduct = ({ heading }) => {
  const productData = useSelector((state) => state.product.productList);
  const categoryList = [...new Set(productData.map((el) => el.farmername))];

  //filter data display
  const [filterby, setFilterBy] = useState("");
  const [dataFilter, setDataFilter] = useState([]);

  useEffect(() => {
    setDataFilter(productData);
  }, [productData]);

  const handleFilterProduct = (farmername) => {
    setFilterBy()
    const filter = productData.filter(
      (el) => el.farmername.toLowerCase() === farmername.toLowerCase()
    );
    setDataFilter(() => {
      return [...filter];
    });
  };
  

  const loadingArrayFeature = new Array(10).fill(null);

  return (
    <div className="my-5">
      <h2 className="font-bold text-2xl text-center text-white bg-yellow-500 rounded-full mb-5">{heading}</h2>

      <div className="flex gap-9 justify-center overflow-scroll scrollbar-none">
        {categoryList[0] ? (
          categoryList.map((el) => {
            return (
              <FilterProduct
                category={el}
                key={el}
                isActive={el.toLowerCase() === filterby.toLowerCase()}
                onClick={() => handleFilterProduct(el)}
              />
            );
          })
        ) : (
          <div className="min-h-[150px] flex justify-center items-center">
            <p className="font-bold text-yellow-600">Loading...</p>
          </div>
        )}
      </div>

      <div className="flex flex-wrap justify-center gap-9 my-6">
        {dataFilter[0]
          ? dataFilter.map((el) => {
              return (
                <CardFeature
                  key={el._id}
                  id={el._id}
                  image={el.image}
                  name={el.name}
                  category={el.category}
                  farmername={el.farmername}
                  quantity={el.quantity}
                  price={el.price}
                />
              );
            })
          : 
          loadingArrayFeature.map((el,index) => (
              <CardFeature loading="Loading..." key={index+"allProduct"} />
            ))}
      </div>
    </div>
  );
};

export default AllProduct;
