import React, { useState } from "react";
import { toast } from "react-hot-toast"; // Import the 'toast' variable from the relevant library
import { useSelector, useDispatch } from "react-redux";
// ... (other imports)

const Orderstatus = () => {
  const [orderIdToCheck, setOrderIdToCheck] = useState(""); // State to hold the order ID for status checking
  const [orderStatus, setOrderStatus] = useState(""); // State to hold the order status

  // Function to check order status
  const checkOrderStatus = (orderId) => {
    // Simulating status retrieval from an API or database
    const orderStatuses = {
      "123456": "Processing",
      "789012": "Shipped",
      "345678": "Delivered",
      // Add more order IDs and statuses as needed
    };

    const status = orderStatuses[orderId];

    if (status) {
      setOrderStatus(`Order ID ${orderId} status: ${status}`);
    } else {
      setOrderStatus(`Order ID ${orderId} not found or status not available`);
    }
  };

  // Function to handle checking order status
  const handleCheckOrderStatus = () => {
    if (orderIdToCheck.trim() === "") {
      // Check if order ID is provided
      toast.error("Please provide an order ID.");
      return;
    }

    // Call function to check order status
    checkOrderStatus(orderIdToCheck);
  };

  return (
    <div className="justify-center mx- flex w-auto ">
      {/* ... (existing JSX) */}
      <form className=" py-3 flex flex-col text-black px-1 border-4 border-yellow-300">
      <div className="flex flex-col items-center">
        <input
          type="text"
          placeholder="Enter Order ID"
          value={orderIdToCheck}
          onChange={(e) => setOrderIdToCheck(e.target.value)}
          className="border p-2 m-2"
        />
        <button className="bg-yellow-500 text-white font-bold py-2 px-4" onClick={handleCheckOrderStatus}>
          Check Order Status
        </button>
        {orderStatus && (
          <div className="bg-yellow-200 text-yellow-800 p-3 m-3">
            <strong>{orderStatus}</strong>
          </div>
        )}
      </div>

      </form>
      
      {/* ... (existing JSX) */}
    </div>
  );
};

export default Orderstatus;
