import React, { useState, useEffect } from "react";
import axios from "axios";

const Order = () => {
  const [orderItems, setOrderItems] = useState([]);

  useEffect(() => {
    fetchCartItem();
  }, []);

  const fetchCartItem = async () => {
    try {
      // Make a GET request to your backend endpoint to fetch order details
      const response = await axios.get("http://localhost:8080/v"); // Replace with your endpoint

      if (response.status === 200) {
        setOrderItems(response.data); // Assuming response.data contains order details
      } else {
        console.error("Failed to fetch order details");
      }
    } catch (error) {
      console.error("Error fetching order details:", error);
    }
  };

  return (
    <div>
      <h2>My Orders</h2>
      <table>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
            {/* Add more table headers if needed */}
          </tr>
        </thead>
        <tbody>
          {orderItems.map((item) => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td>{item.quantity}</td>
              <td>{item.total}</td>
              {/* Add more table cells if needed */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Order;
