import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import { useSelector } from "react-redux";

const Orderstatus = () => {
  const [products, setProducts] = useState([]);
  const userToken = useSelector((state) => state.user.token); // Assuming token is stored in Redux state

  useEffect(() => {
    fetchUserProducts();
  }, [userToken]);

  const fetchUserProducts = async () => {
    if (userToken) {
      try {
        const response = await fetch(`${process.env.REACT_APP_SERVER_DOMAIN}/user/products`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${userToken}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProducts(data.products); // Assuming the response contains an array of products
        } else {
          toast.error("Failed to fetch user products");
        }
      } catch (error) {
        console.error("Error fetching user products:", error);
        toast.error("Error fetching user products");
      }
    }
  };

  return (
    <div className="justify-center my-40 flex w-auto">
      <div className="py-3 flex flex-col text-black px-1 border-4 border-yellow-300">
        {products.length > 0 ? (
          <ul>
            {products.map((product) => (
              <li key={product.id}>
                <p>{product.name}</p>
                {/* Render more product details here */}
              </li>
            ))}
          </ul>
        ) : (
          <p>No products found for the user.</p>
        )}
      </div>
    </div>
  );
};

export default Orderstatus;
