import React, { useState } from "react";
import "./offerballoon.css"

const OfferBalloon = ({ content }) => {
  const [isTooltipVisible, setIsTooltipVisible] = useState(false);

  const balloonStyles = {
    position: "relative",
    display: "inline-block",
  };

  const tooltipStyles = {
    // visibility: isTooltipVisible ? "visible" : "hidden",
    width: "120px",
    backgroundColor: "black",
    color: "#fff",
    textAlign: "center",
    borderRadius: "6px",
    padding: "5px",
    position: "absolute",
    zIndex: "1",
    bottom: "125%",
    left: "50%",
    marginLeft: "60px", // Adjust this value to center the tooltip horizontally
  };

  const handleMouseEnter = () => {
    setIsTooltipVisible(false);
  };

  const handleMouseLeave = () => {
    setIsTooltipVisible(true);
  };

  return (
    <div className="offer-balloon" >
      <span
        className="tooltip"
        // style={tooltipStyles}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        {content}
      </span>
    </div>
  );
};

export default OfferBalloon;
