const nodemailer = require('nodemailer');

const transporter = nodemailer.createTransport({
  service: 'gmail',
  auth: {
    user: 'your_email@gmail.com', // Replace with your email address
    pass: 'your_password', // Replace with your email password (consider using environment variables)
  },
});

const sendEmail = async (formData) => {
  // Destructure form data (assuming formData is an object)
  const { name, email, message } = formData;

  try {
    let info = await transporter.sendMail({
      from: email, // Sender email address
      to: 'veeravikaas@gmail.com', // Receiver email address
      subject: 'Form Submission',
      text: `
        Name: ${name}
        Email: ${email}
        Message: ${message}
      `,
    });

    console.log('Email sent:', info.response);
    return { success: true, message: 'Email sent successfully!' };
  } catch (error) {
    console.error('Error occurred:', error);
    return { success: false, message: 'Failed to send email.' };
  }
};

module.exports = sendEmail;
