import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import { useSelector } from "react-redux";

const Orderstatus = () => {
  const [products, setProducts] = useState([]);
  const userToken = useSelector((state) => state.user.token); // Assuming token is stored in Redux state

  useEffect(() => {
    fetchUserProducts();
  }, [userToken]);

  const fetchUserProducts = async () => {
    if (userToken) {
      try {
        const response = await fetch(`${process.env.REACT_APP_SERVER_DOMAIN}/user/products`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${userToken}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProducts(data.products); // Assuming the response contains an array of products
        } else {
          toast.error("Failed to fetch user products");
        }
      } catch (error) {
        console.error("Error fetching user products:", error);
        toast.error("Error fetching user products");
      }
    }
  };


  return (
    <div className="justify-center my-40 flex w-auto ">
      {/* ... (existing JSX) */}
      <form className=" py-3 flex flex-col text-black px-1 border-4 border-yellow-300">
      <div className="flex flex-col items-center">
        <input
          type="text"
          placeholder="Enter Order ID"
          value={orderIdToCheck}
          onChange={(e) => setOrderIdToCheck(e.target.value)}
          className="border p-2 m-2"
        />
        <button className="bg-yellow-500 text-white font-bold py-2 px-4" onClick={handleCheckOrderStatus}>
          Check Order Status
        </button>
        {orderStatus && (
          <div className="bg-yellow-200 text-yellow-800 p-3 m-3">
            <strong>{orderStatus}</strong>
          </div>
        )}
      </div>

      </form>
      
      {/* ... (existing JSX) */}
    </div>
  );
};

export default Orderstatus;
