import React, { useState } from 'react';
import { toast } from 'react-hot-toast';
import { BsCloudUpload } from 'react-icons/bs';
import { ImagetoBase64 } from '../utility/ImagetoBase64';

const Newproduct = () => {
  const [data, setData] = useState({
    name: '',
    category: '',
    image: '',
    quantity: '',
    price: '',
    description: '',
  });

  const handleOnChange = (e) => {
    const { name, value } = e.target;

    setData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const uploadImage = async (e) => {
    const imageData = await ImagetoBase64(e.target.files[0]);

    setData((prev) => ({
      ...prev,
      image: imageData,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { name, image, category, price, quantity } = data;

    if (name && image && category && price && quantity) {
      const fetchData = await fetch(`${process.env.REACT_APP_SERVER_DOMIN}/uploadProduct`, {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const fetchRes = await fetchData.json();

      console.log(fetchRes);
      toast(fetchRes.message);

      setData({
        name: '',
        category: '',
        image: '',
        price: '',
        quantity: '',
        description: '',
      });
    } else {
      toast('Enter required Fields');
    }
  };

  const formStyles = {
    padding: '4rem',
    margin: 'auto',
    maxWidth: '400px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    backgroundColor: '#ffffff',
  };

  const inputStyles = {
    width: 'calc(100% - 20px)',
    padding: '8px',
    margin: '5px 0',
    border: '1px solid #ccc',
    borderRadius: '4px',
    boxSizing: 'border-box',
    backgroundImage:""
  };

  const selectStyles = {
    ...inputStyles, // Spread the inputStyles
    height: '40px',
    // Additional select styles if needed
  };

  const buttonStyles = {
    ...inputStyles,
    backgroundColor: '#f59e0b',
    color: '#fff',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  };

  const imageUploadContainerStyles = {
    width: 'calc(100% - 20px)',
    height: '150px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    border: '1px dashed #ccc',
    borderRadius: '4px',
    overflow: 'hidden',
    position: 'relative',
    marginBottom: '10px',
    backgroundColor: '#ffff00', // Example color
  };

  const imagePreviewStyles = {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  };

  return (
    <div style={formStyles}>
      <form className="m-auto w-full max-w-md shadow flex flex-col p-3 bg-white" onSubmit={handleSubmit}>
        <label htmlFor='name'>Name</label>
        <input type={"text"}  name="name" className='bg-yellow-300 p-1 my-1' onChange={handleOnChange} value={data.name}/>

        <label htmlFor='category'>Category</label>
        <select className='bg-yellow-300 p-1 my-1' id='category' name='category' onChange={handleOnChange} value={data.category}>
          {/* <option value={"other"}>select category</option> */}
          <option value={"fruits"}>Fruits</option>
          <option value={"vegetable"}>Vegetable</option>
          <option value={"rice"}>rice</option>
          <option value={"grains"}>Grains</option>
        </select>

        <label htmlFor='image'>Image
        <div  className='h-40 w-full bg-yellow-300  rounded flex items-center justify-center cursor-pointer'>
            {
              data.image ? <img src={data.image} className="h-full" /> :<span className='text-5xl'><BsCloudUpload/></span> 
            }
            
            
           <input type={"file"} accept="image/*" id="image" onChange={uploadImage} className="hidden"/>
        </div>
        </label>
        

        <label htmlFor='price' className='my-1'>Price</label>
        <input type={"text"} className='bg-yellow-300 p-1 my-1' name='price' onChange={handleOnChange} value={data.price}/>

        <label htmlFor='quantity' className='my-1'>Quantity( in kg )</label>
        <input type={"text"} className='bg-yellow-300 p-1 my-1' name='quantity' onChange={handleOnChange} value={data.quantity}/>

        <label htmlFor='description'>Description</label>
        <textarea rows={2} value={data.description} className='bg-yellow-300 p-1 my-1 resize-none' name='description' onChange={handleOnChange}></textarea>

        <button className='bg-yellow-500 hover:bg-yellow-600 text-white text-lg font-medium my-2 drop-shadow'>Save</button>
       </form>
    </div>
  )
}

export default Newproduct
