import React from "react";
import { useSelector } from "react-redux";
import CartProduct from "../component/cartProduct";
import emptyCartImage from "../assest/empty3.jpg"
import { toast } from "react-hot-toast";
import {loadStripe} from '@stripe/stripe-js';
import { useNavigate } from "react-router-dom";
import "./cart.css"


const Cart = () => {
  const productCartItem = useSelector((state) => state.product.cartItem);
  const user = useSelector(state => state.user)

  const navigate = useNavigate()

  const totalPrice = productCartItem.reduce(
    (acc, curr) => acc + parseInt(curr.total),
    0
  );
  const totalQty = productCartItem.reduce(
    (acc, curr) =>acc + curr.qty,
    0
  );

  
  
  const handlePayment = async () => {
    if (user.email) {
      const stripePromise = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
      
      try {
        const res = await fetch(`${process.env.REACT_APP_SERVER_DOMIN}/create-checkout-session`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(productCartItem)
        });
  
        if (!res.ok) {
          throw new Error("Error creating checkout session");
        }
  
        const sessionData = await res.json();
  
        const stripe = await stripePromise;
        const { error } = await stripe.redirectToCheckout({ sessionId: sessionData.id });
  
        if (error) {
          toast.error("Failed to redirect to Stripe for payment");
        }
      } catch (error) {
        console.error("Payment error:", error);
        toast.error("Payment failed. Please try again.");
      }
    } else {
      toast.error("You are not logged in!");
      setTimeout(() => {
        navigate("/login");
      }, 1000);
    }
  };
  
  return (

    <div className="i">
      <div className=" car1 p-5 md:p-4">
        <h2 className="text-lg md:text-6xl font-bold text-yellow-500">
          Your 
          <span className="text-white"> Cart Items</span>
        </h2>

        {productCartItem[0] ?
        <div className="my-8 flex gap-9">
          {/* display cart items  */}
          <div className="w-full max-w-3xl ">
            {productCartItem.map((el) => {
              return (
                <CartProduct
                  key={el._id}
                  id={el._id}
                  name={el.name}
                  image={el.image}
                  category={el.category}
                  quantity={el.quantity}
                  qty={el.qty}
                  total={el.total}
                  price={el.price}
                />
              );
            })}
          </div>

          {/* total cart item  */}
          <div className="cart2 w-full max-w-md p-7 ml-auto mx-10 h-fullnpm border-8 border-yellow-300  " >
            <h2 className="bg-yellow-500 text-white p-2 text-lg">Summary</h2>
            <div className="flex w-full py-2 text-lg text-white font-bold border-b">
              <p>Total Qty :</p>
              <p className="ml-auto w-32 font-bold">
                <span>{totalQty}</span>
                <span className="font-bold text-yellow-500">Kg</span>
                
              </p>
            </div>
            <div className="flex w-full py-2 text-lg text-white font-bold border-b">
              <p>Total Price :</p>
              <p className="ml-auto w-32 font-bold">
                <span className="text-yellow-500">â‚¹</span> {totalPrice}
              </p>
            </div>
            <button className="bg-yellow-500 w-full text-lg font-bold py-2 text-white" onClick={handlePayment}>
              Payment
            </button>
          </div>
        </div>

        : 
        <>
          <div className="flex w-full justify-center items-center flex-col">
            <img src={emptyCartImage} className="w-full max-w-sm"/>
            <p className="text-yellow-500 text-3xl font-bold">Empty Cart</p>
          </div>
        </>
      }
      </div>
      </div>
  );
};

export default Cart;
