import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import { useSelector } from "react-redux";

const Orderstatus = () => {
  const [cart, setCart] = useState([]);
  const [orderStatus, setOrderStatus] = useState('');
  const userToken = useSelector((state) => state.user.token);

  useEffect(() => {
    fetchUserCart();
    fetchOrderStatus();
  }, [userToken]);

  const fetchUserCart = async () => {
    if (userToken) {
      try {
        const response = await fetch(`${process.env.REACT_APP_SERVER_DOMAIN}/user/cart`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${userToken}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setCart(data.cartItems || []);
        } else {
          toast.error("Failed to fetch user's cart");
        }
      } catch (error) {
        console.error("Error fetching user's cart:", error);
        toast.error("Error fetching user's cart");
      }
    }
  };

  const fetchOrderStatus = async () => {
    // Fetch order status for the user using a similar approach as fetchUserCart
    // Set the order status using setOrderStatus
  };

  return (
    <div className="justify-center my-40 flex w-auto">
      <div className="py-3 flex flex-col text-black px-1 border-4 border-yellow-300">
        <h2>User Cart</h2>
        {cart.length > 0 ? (
          <ul>
            {cart.map((item) => (
              <li key={item._id}>
                <p>{item.name}</p>
                {/* Render more item details here */}
              </li>
            ))}
          </ul>
        ) : (
          <p>No items in the cart for the user.</p>
        )}

        <h2>Order Status</h2>
        <p>{orderStatus}</p>
        {/* Render additional order status details here */}
      </div>
    </div>
  );
};

export default Orderstatus;
